#include <iostream>#include <string>#include <iomanip>using namespace std;string translation(string);int main(){Êstring phrase;Êcout << "Enter a sentence:" << endl;Êgetline (cin, phrase);Êphrase = translation(phrase);Êcout << phrase << endl;}string translation(string word){Êstring translateWord, translateSentence = "";Êint length = 0, index = 0;Êwhile (word[index] != '\0')ÊÊÊ{ÊÊÊÊÊif (word.find (' ', index) != -1)ÊÊÊÊÊ{ÊÊÊÊÊÊÊif (word.find(' ',index) == 0)ÊÊÊÊÊÊÊÊÊ{ÊÊÊÊÊÊÊÊÊÊÊindex++;ÊÊÊÊÊÊÊÊÊÊÊcontinue;ÊÊÊÊÊÊÊÊÊ}ÊÊÊÊÊÊÊif(word.substr(index,1) == " " )ÊÊÊÊÊÊÊÊÊ{ÊÊÊÊÊÊÊÊÊÊÊindex++;ÊÊÊÊÊÊÊÊÊÊÊcontinue;ÊÊÊÊÊÊÊÊÊÊÊ}ÊÊÊÊÊlength = word.find(' ', index);ÊÊÊÊÊlength -= index;ÊÊÊÊÊtranslateWord = word.substr(index, length);ÊÊÊÊÊtranslateWord.insert(length, "ay");ÊÊÊÊÊtranslateWord.insert(length, 1, word[index]);ÊÊÊÊÊtranslateWord.erase(0,1);ÊÊÊÊÊindex += length ;ÊÊÊÊÊ}ÊelseÊÊÊÊÊ{ÊÊÊÊÊtranslateWord = word.substr(index);ÊÊÊÊÊlength = translateWord.length();ÊÊÊÊÊtranslateWord.insert(length, "ay");ÊÊÊÊÊtranslateWord.insert(length,1, word[index]);ÊÊÊÊÊtranslateWord.erase (0, 1);ÊÊÊÊÊindex = word.length();ÊÊÊÊÊ}ÊtranslateSentence += (translateWord + " ");ÊÊÊ}return translateSentence;}